class TipCalculator{_value="";_billValue="";_peopleValue="";_customTipPercValue="";_tipPercValue="";constructor(){this.billInput=document.querySelector("#billInput"),this.peopleInput=document.querySelector("#peopleInput"),this.customTipInput=document.querySelector("#customTipInput"),this.totalPriceOutput=document.querySelector("#totalPriceOutput"),this.totalTipOutput=document.querySelector("#totalTipOutput"),this.peopleInputLabel=document.querySelector(".people--label"),this.billInputLabel=document.querySelector(".bill--label")}get value(){return this._value}set value(t){this._value=t}get billValue(){return this._billValue}set billValue(t){this._billValue=t}get peopleValue(){return this._peopleValue}set peopleValue(t){this._peopleValue=t}get customTipPercValue(){return this._customTipPercValue}set customTipPercValue(t){this._customTipPercValue=t}get tipPercValue(){return this._tipPercValue}set tipPercValue(t){this._tipPercValue=t}storeValue(){document.addEventListener("keyup",(t=>{let e=t.target,l=/^[0-9\.]+$/;"INPUT"==e.tagName&&(e.value.match(l)||(e.value=""),this.value=e.value)}))}setBill(){document.addEventListener("keyup",(t=>{"billInput"==t.target.id&&(this.billValue=this.value)}))}setCustomPercTip(){document.addEventListener("keyup",(t=>{"customTipInput"==t.target.id&&(this.customTipPercValue=this.value,this.tipPercValue=this.customTipPercValue/100)}))}setPeople(){document.addEventListener("keyup",(t=>{"peopleInput"==t.target.id&&(this.peopleValue=this.value)}))}setTipPerc(){document.addEventListener("click",(t=>{let e=t.target;e.classList.contains("tip--input")&&e.classList.contains("-button")&&(this.tipPercValue=parseInt(e.value)/100)}))}calcTipAmount(){let t=this.billValue*this.tipPercValue/this.peopleValue;isNaN(t)||(isFinite(t)?this.totalTipOutput.innerHTML="$"+t.toFixed(2):this.totalTipOutput.innerHTML="$0.00")}calcTotalAmount(){let t=this.billValue*(1+this.tipPercValue)/this.peopleValue;isNaN(t)||(isFinite(t)?this.totalPriceOutput.innerHTML="$"+t.toFixed(2):this.totalPriceOutput.innerHTML="$0.00")}calcAll(){this.calcTipAmount(),this.calcTotalAmount()}validate(t,e){document.addEventListener("keyup",(l=>{l.target;0==e.value&&""!=e.value?(t.classList.add("-error"),e.classList.add("-error")):(t.classList.remove("-error"),e.classList.remove("-error"))}))}validateAll(){this.validate(this.billInputLabel,this.billInput),this.validate(this.peopleInputLabel,this.peopleInput)}reset(){document.addEventListener("click",(t=>{"resetButton"==t.target.id&&(this.billValue=0,this.peopleValue=0,this.customTipPercValue=0,this.tipPercValue=0,this.billInput.value="",this.peopleInput.value="",this.customTipInput.value="",this.totalPriceOutput.innerHTML="$0.00",this.totalTipOutput.innerHTML="$0.00")}))}}let tipCalculator=new TipCalculator;tipCalculator.storeValue(),tipCalculator.setBill(),tipCalculator.setCustomPercTip(),tipCalculator.setTipPerc(),tipCalculator.setPeople(),tipCalculator.validateAll(),tipCalculator.reset(),setInterval((()=>{tipCalculator.calcAll()}),500);